  const BASE_URL = 'http://localhost:5000/api/auth';

export const signup = async (userData) => {
  const response = await fetch(`${BASE_URL}/signup`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(userData),
  });

  return response.json();
};


export const login = async (userData) => {
  try {
    const response = await fetch('http://localhost:5000/api/auth/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData),
    });

    return await response.json();
  } catch (error) {
    console.error('Login error:', error);
    return { message: 'Server error' };
  }
};


export const createContract = async (contractData) => {
  try {
    const response = await fetch(`${BASE_URL}/create`, {  // Changed from '/create' to match your structure
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('token')}` // Add if using auth
      },
      body: JSON.stringify(contractData)
    });

    const data = await response.json();
    
    if (!response.ok) {
      throw new Error(data.message || 'Failed to create contract');
    }

    return data;
  } catch (error) {
    console.error('Contract creation error:', error);
    throw error;
  }
};
export const getContracts = async () => {
  const response = await fetch(`${BASE_URL}/all`);
  return response.json();
};
