import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginHR } from '../services/api';  // HR-specific login API function
import { Link } from 'react-router-dom';
import './login.css';

function HRLoginPage() {
  const [hr_email, setHrEmail] = useState('');
  const [hr_password, setHrPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleHRLogin = async () => {
    const hrData = { hr_email, hr_password };

    try {
      const response = await loginHR(hrData);
      console.log("HR Login Response:", response); // Debugging response

      if (response?.message === 'No HR account exists' || response?.message === 'Invalid password') {
        setErrorMessage(response.message);
        setSuccessMessage('');
      } else {
        setErrorMessage('');
        setSuccessMessage('Login successful! Redirecting...');

        // Store HR user data in local storage for session management
        localStorage.setItem('hrUser', JSON.stringify(response.hr));

        // Redirect HR to dashboard
        setTimeout(() => navigate('/hr-dashboard'), 1500);
      }
    } catch (error) {
      console.error("HR Login Error:", error);
      setErrorMessage('Error logging in. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <div className="form-box">
        <h2>HR Login</h2>
        <input
          type="email"
          placeholder="HR Email"
          value={hr_email}
          onChange={(e) => setHrEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={hr_password}
          onChange={(e) => setHrPassword(e.target.value)}
        />
        <button onClick={handleHRLogin}>Login</button>

        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}

        {successMessage && (
          <div className="success-box">
            {successMessage}
          </div>
        )}

        <p>Don't have an account? <Link to="/hr-signup">Sign Up</Link></p>
      </div>
    </div>
  );
}

export default HRLoginPage;
