import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './recommendation.css';

function RecommendationPage() {
  const navigate = useNavigate();

  // State variables
  const [jobIds, setJobIds] = useState([]);
  const [hrIds, setHrIds] = useState([]);
  const [recommendationTypes, setRecommendationTypes] = useState(["Highly Recommended", "Recommended", "Not Recommended"]);
  const [selectedJob, setSelectedJob] = useState("");
  const [selectedHR, setSelectedHR] = useState("");
  const [selectedRecommendation, setSelectedRecommendation] = useState("");

  // Fetch Job IDs and HR IDs from the database
  useEffect(() => {
    fetch("/api/job-ids") // Replace with actual API endpoint
      .then(response => response.json())
      .then(data => setJobIds(data))
      .catch(error => console.error("Error fetching job IDs:", error));

    fetch("/api/hr-ids") // Replace with actual API endpoint
      .then(response => response.json())
      .then(data => setHrIds(data))
      .catch(error => console.error("Error fetching HR IDs:", error));
  }, []);

  return (
    <div className="recommendation-container">
      <h2>Provide Recommendation</h2>

      {/* Job ID Dropdown */}
      <label>üîñ Select Job ID:</label>
      <select onChange={(e) => setSelectedJob(e.target.value)} value={selectedJob}>
        <option value="">--Select Job ID--</option>
        {jobIds.map((id) => <option key={id} value={id}>{id}</option>)}
      </select>

      {/* HR ID Dropdown */}
      <label>üë§ Select HR ID:</label>
      <select onChange={(e) => setSelectedHR(e.target.value)} value={selectedHR}>
        <option value="">--Select HR ID--</option>
        {hrIds.map((id) => <option key={id} value={id}>{id}</option>)}
      </select>

      {/* Recommendation Type Dropdown */}
      <label>üìù Select Recommendation:</label>
      <select onChange={(e) => setSelectedRecommendation(e.target.value)} value={selectedRecommendation}>
        <option value="">--Select Recommendation--</option>
        {recommendationTypes.map((rec) => <option key={rec} value={rec}>{rec}</option>)}
      </select>

      {/* Read-only Textarea */}
      <label>üìú Recommendation Details:</label>
      <textarea 
        value={`Job ID: ${selectedJob}\nHR ID: ${selectedHR}\nRecommendation: ${selectedRecommendation}`} 
        readOnly
      />

      {/* Back & Submit Buttons */}
      <div className="recommendation-buttons">
        <button className="recommendation-back" onClick={() => navigate(-1)}>Back</button>
        <button className="recommendation-submit">Submit</button>
      </div>
    </div>
  );
}

export default RecommendationPage;
