import React, { useEffect, useState } from "react";
import axios from "axios";
import "./CreateContract.css";
import { useNavigate } from "react-router-dom";

const CreateContract = () => {
  const [jobs, setJobs] = useState([]);
  const [contractData, setContractData] = useState({
    job_id: "",
    salary: "",
    probation_period: "",
    contract_start_date: "",
    contract_end_date: "",
    benefits: ""
  });
  const [jobDetails, setJobDetails] = useState("");
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  // Get HR ID from localStorage or sessionStorage
  const hr_id = localStorage.getItem("hr_id") || sessionStorage.getItem("hr_id");

  useEffect(() => {
    if (!hr_id) {
      alert("HR ID not found. Please log in again.");
      navigate("/login"); // Redirect to login page
      return;
    }
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/auth/jobs");
      setJobs(response.data.data || []);
    } catch (err) {
      console.error("Error fetching jobs:", err);
      setError("Failed to fetch jobs. Please try again later.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setContractData((prev) => ({
      ...prev,
      [name]: value
    }));

    // When job is selected, show its details
    if (name === "job_id") {
      const selectedJob = jobs.find((job) => job.id === parseInt(value));
      if (selectedJob) {
        setJobDetails(`Job Title: ${selectedJob.title}, Department: ${selectedJob.department}`);
      } else {
        setJobDetails("");
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate all fields
    const { job_id, salary, probation_period, contract_start_date, contract_end_date, benefits } = contractData;
    if (!job_id || !salary || !probation_period || !contract_start_date || !contract_end_date || !benefits) {
      setError("Please fill in all fields");
      return;
    }

    if (!hr_id) {
      setError("HR ID not found. Please log in again.");
      return;
    }

    setError(null);
    setSuccess(null);
    setLoading(true);

    try {
      const response = await axios.post(
        "http://localhost:5000/api/auth/create",
        {
          ...contractData,
          hr_id: parseInt(hr_id)
        },
        {
          headers: {
            "Content-Type": "application/json"
          }
        }
      );

      if (response.data && response.data.message) {
        setSuccess(response.data.message);
        setContractData({
          job_id: "",
          salary: "",
          probation_period: "",
          contract_start_date: "",
          contract_end_date: "",
          benefits: ""
        });
        setJobDetails("");
      }
    } catch (err) {
      console.error("Error creating contract:", err);
      setError(err.response?.data?.message || "Failed to create contract. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="contract-form">
      <h2>Create Job Contract</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Job ID:
          <select name="job_id" value={contractData.job_id} onChange={handleChange} required>
            <option value="">Select a Job</option>
            {jobs.map((job) => (
              <option key={job.id} value={job.id}>
                {job.id} - {job.title}
              </option>
            ))}
          </select>
        </label>
        {jobDetails && <p><strong>{jobDetails}</strong></p>}

        <label>
          Salary:
          <input
            type="number"
            name="salary"
            value={contractData.salary}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Probation Period (months):
          <input
            type="number"
            name="probation_period"
            value={contractData.probation_period}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Contract Start Date:
          <input
            type="date"
            name="contract_start_date"
            value={contractData.contract_start_date}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Contract End Date:
          <input
            type="date"
            name="contract_end_date"
            value={contractData.contract_end_date}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Benefits:
          <textarea
            name="benefits"
            value={contractData.benefits}
            onChange={handleChange}
            required
          />
        </label>

        <button type="submit" disabled={loading}>
          {loading ? "Creating..." : "Create Contract"}
        </button>
      </form>

      {error && <p className="error-message">❌ {error}</p>}
      {success && <p className="success-message">✅ {success}</p>}
    </div>
  );
};

export default CreateContract;
