import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { createContract, getJobs, getHRs } from '../services/api';
import "./contract.css"; 

function ContractPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    job_id: "",
    hr_id: "",
    salary: "",
    probation_period: "",
    start_date: "",
    end_date: "",
    benefits: "",
  });
  const [jobs, setJobs] = useState([]);
  const [hrs, setHrs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch jobs and HRs for dropdowns
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [jobsResponse, hrsResponse] = await Promise.all([
          getJobs(),
          getHRs()
        ]);
        setJobs(jobsResponse.data);
        setHrs(hrsResponse.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to load dropdown data");
        setLoading(false);
        console.error("Error fetching dropdown data:", err);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async () => {
    if (Object.values(formData).some(value => !value)) {
      alert("Please fill in all fields");
      return;
    }

    try {
      await createContract(formData);
      alert("Contract created successfully!");
      navigate(-1);
    } catch (error) {
      console.error("Contract creation error:", error);
      alert(`Failed to create contract: ${error.response?.data?.message || error.message}`);
    }
  };

  if (loading) return <div className="loading">Loading form data...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="contract-page-container">
      <button className="back-button" onClick={() => navigate(-1)}>
        &larr; Back
      </button>
      
      <h2>Create New Contract</h2>
      
      <div className="contract-form">
        <div className="form-group">
          <label>Job Position</label>
          <select 
            name="job_id" 
            value={formData.job_id} 
            onChange={handleChange}
            required
          >
            <option value="">Select a job</option>
            {jobs.map(job => (
              <option key={job.jobid} value={job.jobid}>
                {job.job_title} (ID: {job.jobid})
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>HR Manager</label>
          <select 
            name="hr_id" 
            value={formData.hr_id} 
            onChange={handleChange}
            required
          >
            <option value="">Select an HR manager</option>
            {hrs.map(hr => (
              <option key={hr.hr_id} value={hr.hr_id}>
                {hr.hr_name} (ID: {hr.hr_id})
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Salary</label>
          <input
            type="text"
            name="salary"
            value={formData.salary}
            onChange={handleChange}
            placeholder="e.g., $85,000 per annum"
            required
          />
        </div>

        <div className="form-group">
          <label>Probation Period (months)</label>
          <select
            name="probation_period"
            value={formData.probation_period}
            onChange={handleChange}
            required
          >
            <option value="">Select probation period</option>
            <option value="0">No probation</option>
            <option value="1">1 month</option>
            <option value="3">3 months</option>
            <option value="6">6 months</option>
          </select>
        </div>

        <div className="form-group">
          <label>Start Date</label>
          <input
            type="date"
            name="start_date"
            value={formData.start_date}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>End Date</label>
          <input
            type="date"
            name="end_date"
            value={formData.end_date}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Benefits</label>
          <select
            name="benefits"
            value={formData.benefits}
            onChange={handleChange}
            required
          >
            <option value="">Select benefits package</option>
            <option value="Basic: Health insurance">Basic: Health insurance</option>
            <option value="Standard: Health + 401k">Standard: Health + 401k</option>
            <option value="Premium: Full benefits package">Premium: Full benefits package</option>
            <option value="Custom">Custom (specify below)</option>
          </select>
          {formData.benefits === "Custom" && (
            <input
              type="text"
              name="custom_benefits"
              placeholder="Enter custom benefits"
              onChange={(e) => setFormData({...formData, benefits: e.target.value})}
              className="custom-benefits-input"
            />
          )}
        </div>

        <button className="submit-button" onClick={handleSubmit}>
          Create Contract
        </button>
      </div>
    </div>
  );
}

export default ContractPage;
