import React, { useState, useEffect } from "react"; 
import { useNavigate } from "react-router-dom"; 
import { createContract } from '../services/api'; // Import API call 
import "./contract.css";  
 
function ContractPage() { 
  const navigate = useNavigate(); 
  const [formData, setFormData] = useState({ 
    job_id: "", 
    hr_id: "", // This will be set from localStorage/sessionStorage or default to 1
    salary: "", 
    probation_period: "", 
    start_date: "", 
    end_date: "", 
    benefits: "", 
  }); 

  // Get HR ID using the specified logic when component mounts
  useEffect(() => {
    const hr_id = localStorage.getItem("hr_id") || sessionStorage.getItem("hr_id") || 1;
    
    setFormData(prevData => ({
      ...prevData,
      hr_id: hr_id
    }));
  }, []);
 
  const handleChange = (e) => { 
    setFormData((prevData) => ({ 
      ...prevData, 
      [e.target.name]: e.target.value, 
    })); 
  }; 
 
  const handleSubmit = async () => {
    // Check only user-fillable fields
    const visibleFields = ['job_id', 'salary', 'probation_period', 'start_date', 'end_date', 'benefits'];
    const emptyFields = visibleFields.filter(field => !formData[field]);
    
    if (emptyFields.length > 0) { 
      alert("Please fill in all fields before submitting."); 
      return; 
    } 
 
    try { 
      const response = await createContract(formData); 
      alert("Contract Created Successfully!"); 
      console.log(response); 
      navigate(-1); // Navigate back after submission 
    } catch (error) { 
      console.error("Error creating contract:", error); 
      alert("Failed to create contract."); 
    } 
  }; 
 
  return ( 
    <div className="contract-page-container"> 
      <button className="contract-back-button" onClick={() => navigate(-1)}>Back</button> 
      <h2>Contract</h2> 
      <div className="contract-content"> 
        <div className="contract-form"> 
          <label>Job ID</label> 
          <input type="text" name="job_id" value={formData.job_id} onChange={handleChange} /> 
 
          <label>Salary</label> 
          <input type="text" name="salary" value={formData.salary} onChange={handleChange} /> 
 
          <label>Probation Period</label> 
          <input type="number" name="probation_period" value={formData.probation_period} onChange={handleChange} /> 
 
          <label>Contract Start Date</label> 
          <input type="date" name="start_date" value={formData.start_date} onChange={handleChange} /> 
 
          <label>Contract End Date</label> 
          <input type="date" name="end_date" value={formData.end_date} onChange={handleChange} /> 
 
          <label>Benefits</label> 
          <input type="text" name="benefits" value={formData.benefits} onChange={handleChange} /> 
        </div>
      </div> 
 
      <button className="contract-submit-button" onClick={handleSubmit}>Submit</button> 
    </div> 
  ); 
} 
 
export default ContractPage;
