import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./contract.css"; // Optional: use if you have styling

function ContractPage() {
  const navigate = useNavigate();

  // Get the HR ID from localStorage or sessionStorage
  const hr_id = localStorage.getItem("hr_id") || sessionStorage.getItem("hr_id") || 1;

  // Form States
  const [jobId, setJobId] = useState("");
  const [salary, setSalary] = useState("");
  const [probationPeriod, setProbationPeriod] = useState("");
  const [contractStartDate, setContractStartDate] = useState("");
  const [contractEndDate, setContractEndDate] = useState("");
  const [benefits, setBenefits] = useState("");
  const [statusMessage, setStatusMessage] = useState(null);

  // Submit Handler
  const handleContractSubmit = async (e) => {
    e.preventDefault();
    setStatusMessage(null);

    try {
      const response = await axios.post("http://localhost:5000/api/contracts", {
        job_id: jobId,
        hr_id: parseInt(hr_id),
        salary,
        probation_period: probationPeriod,
        contract_start_date: contractStartDate,
        contract_end_date: contractEndDate,
        benefits,
      });

      if (response.data?.success) {
        setStatusMessage({ type: "success", message: "✅ Contract created successfully." });
        // Clear form
        setJobId("");
        setSalary("");
        setProbationPeriod("");
        setContractStartDate("");
        setContractEndDate("");
        setBenefits("");
      } else {
        setStatusMessage({ type: "error", message: "❌ Failed to create contract." });
      }
    } catch (error) {
      console.error("Contract creation error:", error);
      setStatusMessage({ type: "error", message: "⚠️ An error occurred. Please try again." });
    }
  };

  return (
    <div className="contract-container">
      <h2>Create Job Contract</h2>

      {statusMessage && (
        <div className={`status-message ${statusMessage.type}`}>
          {statusMessage.message}
        </div>
      )}

      <form onSubmit={handleContractSubmit} className="contract-form">
        <div className="form-group">
          <label>📄 Job ID:</label>
          <input
            type="text"
            value={jobId}
            onChange={(e) => setJobId(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>💰 Salary:</label>
          <input
            type="text"
            value={salary}
            onChange={(e) => setSalary(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>⏳ Probation Period (in months):</label>
          <input
            type="number"
            value={probationPeriod}
            onChange={(e) => setProbationPeriod(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>📅 Contract Start Date:</label>
          <input
            type="date"
            value={contractStartDate}
            onChange={(e) => setContractStartDate(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>📅 Contract End Date:</label>
          <input
            type="date"
            value={contractEndDate}
            onChange={(e) => setContractEndDate(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>🎁 Benefits:</label>
          <textarea
            value={benefits}
            onChange={(e) => setBenefits(e.target.value)}
            placeholder="Enter benefits like health insurance, bonuses, etc."
            rows="4"
            required
          />
        </div>

        <div className="form-actions">
          <button type="submit" className="primary-button">Submit</button>
          <button type="button" className="secondary-button" onClick={() => navigate(-1)}>Back</button>
        </div>
      </form>
    </div>
  );
}

export default ContractPage;
