import { useState, useEffect } from "react";
import axios from "axios";
import "./contract.css";

function ContractPage() {
  const [jobId, setJobId] = useState("");
  const [hrId, setHrId] = useState("");
  const [salary, setSalary] = useState("");
  const [probationPeriod, setProbationPeriod] = useState("");
  const [contractStartDate, setContractStartDate] = useState("");
  const [contractEndDate, setContractEndDate] = useState("");
  const [benefits, setBenefits] = useState("");
  const [sendStatus, setSendStatus] = useState({ type: null, message: "" });

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser?.hr_id) {
      setHrId(storedUser.hr_id);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSendStatus({ type: null, message: "" });

    if (!jobId || !hrId || !salary || !probationPeriod || !contractStartDate || !contractEndDate) {
      setSendStatus({ type: "error", message: "All fields are required!" });
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/contracts", {
        job_id: jobId,
        hr_id: hrId,
        salary,
        probation_period: probationPeriod,
        contract_start_date: contractStartDate,
        contract_end_date: contractEndDate,
        benefits,
      });

      if (response.data?.success) {
        setSendStatus({ type: "success", message: "Contract created successfully!" });
      } else {
        setSendStatus({ type: "error", message: response.data?.message || "Failed to create contract." });
      }
    } catch (err) {
      console.error("Error creating contract:", err);
      setSendStatus({ type: "error", message: "Failed to create contract. Please try again." });
    }
  };

  return (
    <div className="contract-container">
      <h2>Create Contract</h2>

      {sendStatus.type && (
        <div className={`status-message ${sendStatus.type}`}>
          {sendStatus.message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* Other inputs remain the same */}

        <div className="form-group">
          <label>HR ID:</label>
          <input
            type="number"
            value={hrId}
            readOnly
          />
        </div>

        {/* Submit button */}
        <div className="form-actions">
          <button type="submit" className="primary-button">Create Contract</button>
        </div>
      </form>
    </div>
  );
}

export default ContractPage;
