import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./communication.css";

function CommunicationPage() {
  const navigate = useNavigate();

  // Sample applicant IDs (Replace with API call if needed)
  const applicants = [
    { id: "101", name: "John Doe" },
    { id: "102", name: "Jane Smith" },
    { id: "103", name: "Michael Johnson" }
  ];

  const [selectedApplicant, setSelectedApplicant] = useState("");
  const [message, setMessage] = useState("");

  const handleSendMessage = () => {
    if (!selectedApplicant || !message) {
      alert("Please select an Applicant and enter a message.");
      return;
    }

    console.log(`Message sent to Applicant ${selectedApplicant}: ${message}`);
    alert("Message sent successfully!");
    setMessage(""); // Clear message after sending
  };

  return (
    <div className="communication-container">
      <h2>Communicate with Applicant</h2>
      <div className="communication-form">
        
        <label>Select Applicant:</label>
        <select
          value={selectedApplicant}
          onChange={(e) => setSelectedApplicant(e.target.value)}
        >
          <option value="">-- Select Applicant --</option>
          {applicants.map((applicant) => (
            <option key={applicant.id} value={applicant.id}>
              {applicant.name} (ID: {applicant.id})
            </option>
          ))}
        </select>

        <label>Message:</label>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />

        <button className="send-button" onClick={handleSendMessage}>
          Send
        </button>
        <button className="back-button" onClick={() => navigate(-1)}>
          Back
        </button>
      </div>
    </div>
  );
}

export default CommunicationPage;
