import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./recommendation.css";

function RecommendationPage() {
  const navigate = useNavigate();
  const [applicants, setApplicants] = useState([]);
  const [hrs, setHrs] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [selectedApplicant, setSelectedApplicant] = useState("");
  const [selectedHR, setSelectedHR] = useState("");
  const [recommendationText, setRecommendationText] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitStatus, setSubmitStatus] = useState({ type: null, message: "" });

  // Fetch all necessary data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const [applicantsRes, hrsRes] = await Promise.all([
          axios.get("http://localhost:5000/api/auth/applicants"),
          axios.get("http://localhost:5000/api/auth/hrs")
        ]);

        setApplicants(applicantsRes.data?.data || []);
        setHrs(hrsRes.data?.data || []);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError("Failed to load initial data. Please refresh the page.");
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, []);

  // Fetch recommendations for selected applicant when it changes
  useEffect(() => {
    if (selectedApplicant) {
      const fetchApplicantRecommendations = async () => {
        try {
          const response = await axios.get(
            `http://localhost:5000/api/auth/recommendations/applicant/${selectedApplicant}`
          );
          setRecommendations(response.data || []);
        } catch (err) {
          console.error("Error fetching recommendations:", err);
        }
      };
      fetchApplicantRecommendations();
    }
  }, [selectedApplicant]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitStatus({ type: null, message: "" });

    if (!selectedApplicant || !selectedHR || !recommendationText) {
      setSubmitStatus({ type: "error", message: "Please fill in all fields" });
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:5000/api/auth/recommendations",
        {
          hr_id: selectedHR,
          applicant_id: selectedApplicant,
          recommendation: recommendationText
        }
      );

      if (response.data) {
        setSubmitStatus({
          type: "success",
          message: "Recommendation submitted successfully!"
        });
        // Reset form
        setSelectedApplicant("");
        setSelectedHR("");
        setRecommendationText("");
        // Refresh recommendations list
        const recResponse = await axios.get(
          `http://localhost:5000/api/auth/recommendations/applicant/${selectedApplicant}`
        );
        setRecommendations(recResponse.data || []);
      }
    } catch (err) {
      console.error("Submission error:", err);
      setSubmitStatus({
        type: "error",
        message: err.response?.data?.message || "Failed to submit recommendation."
      });
    }
  };

  if (isLoading) {
    return (
      <div className="recommendation-container">
        <div className="loading-spinner"></div>
        <p>Loading data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="recommendation-container error">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => window.location.reload()}>Refresh Page</button>
      </div>
    );
  }

  return (
    <div className="recommendation-container">
      <h2>Provide Recommendation</h2>

      {submitStatus.type && (
        <div className={`status-message ${submitStatus.type}`}>
          {submitStatus.message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* HR Dropdown */}
        <div className="form-group">
          <label>üîñ Select HR:</label>
          <select
            onChange={(e) => setSelectedHR(e.target.value)}
            value={selectedHR}
            required
            disabled={!hrs.length}
          >
            <option value="">--Select HR--</option>
            {hrs.map((hr) => (
              <option key={hr.hr_id} value={hr.hr_id}>
                {hr.hr_name || `HR ${hr.hr_id}`}
              </option>
            ))}
          </select>
          {!hrs.length && <p className="form-hint">No HRs available</p>}
        </div>

        {/* Applicant Dropdown */}
        <div className="form-group">
          <label>üë§ Select Applicant:</label>
          <select
            onChange={(e) => setSelectedApplicant(e.target.value)}
            value={selectedApplicant}
            required
            disabled={!applicants.length}
          >
            <option value="">--Select Applicant--</option>
            {applicants.map((applicant) => (
              <option key={applicant.applicant_id} value={applicant.applicant_id}>
                {applicant.username || `Applicant ${applicant.applicant_id}`}
              </option>
            ))}
          </select>
          {!applicants.length && <p className="form-hint">No applicants available</p>}
        </div>

        {/* Recommendation Text */}
        <div className="form-group">
          <label>üìù Recommendation Description:</label>
          <textarea
            value={recommendationText}
            onChange={(e) => setRecommendationText(e.target.value)}
            required
            rows="4"
            placeholder="Enter detailed recommendation..."
          />
        </div>

        {/* Form Actions */}
        <div className="form-actions">
          <button
            type="button"
            className="secondary-button"
            onClick={() => navigate(-1)}
          >
            Back
          </button>
          <button
            type="submit"
            className="primary-button"
            disabled={isLoading || !applicants.length || !hrs.length}
          >
            {isLoading ? "Submitting..." : "Submit Recommendation"}
          </button>
        </div>
      </form>

      {/* Existing Recommendations */}
      {recommendations.length > 0 && (
        <div className="recommendations-list">
          <h3>Existing Recommendations</h3>
          <ul>
            {recommendations.map((rec) => (
              <li key={rec.recommendation_id}>
                <p><strong>HR:</strong> {rec.hr_name}</p>
                <p><strong>Recommendation:</strong> {rec.recommendation_description}</p>
                <p><small>Date: {new Date(rec.created_at).toLocaleString()}</small></p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default RecommendationPage;
