import { useState } from "react";
import "./applicantreport.css"; // Import CSS

function ApplicantReportPage() {
  const applicantList = [
    { applicant_id: 1, applicant_name: "David Miller", applicant_email: "david@example.com", applicant_age: 28, applicant_gender: "Male", quali_id: 201, applicant_experience: 5 },
    { applicant_id: 2, applicant_name: "Emma Watson", applicant_email: "emma@example.com", applicant_age: 25, applicant_gender: "Female", quali_id: 202, applicant_experience: 3 },
    { applicant_id: 3, applicant_name: "Noah Brown", applicant_email: "noah@example.com", applicant_age: 30, applicant_gender: "Male", quali_id: 203, applicant_experience: 7 },
  ];

  const [selectedApplicantId, setSelectedApplicantId] = useState("");
  const [applicantData, setApplicantData] = useState(null);

  const handleApplicantSelect = (e) => {
    const selectedId = parseInt(e.target.value);
    setSelectedApplicantId(selectedId);
    const selectedApplicant = applicantList.find((applicant) => applicant.applicant_id === selectedId);
    setApplicantData(selectedApplicant || null);
  };

  return (
    <div className="applicant-report-container">
      <h2>Applicant Report</h2>

      <div className="applicant-select-container">
        <label htmlFor="applicant-select">Select Applicant ID:</label>
        <select id="applicant-select" value={selectedApplicantId} onChange={handleApplicantSelect}>
          <option value="">-- Choose an Applicant --</option>
          {applicantList.map((applicant) => (
            <option key={applicant.applicant_id} value={applicant.applicant_id}>
              {applicant.applicant_id} - {applicant.applicant_name}
            </option>
          ))}
        </select>
      </div>

      {applicantData ? (
        <div className="applicant-details">
          <h3>Applicant Report - {applicantData.applicant_name}</h3>
          <p><strong>ID:</strong> {applicantData.applicant_id}</p>
          <p><strong>Email:</strong> {applicantData.applicant_email}</p>
          <p><strong>Age:</strong> {applicantData.applicant_age}</p>
          <p><strong>Gender:</strong> {applicantData.applicant_gender}</p>
          <p><strong>Qualification ID:</strong> {applicantData.quali_id}</p>
          <p><strong>Experience:</strong> {applicantData.applicant_experience} years</p>
        </div>
      ) : (
        <p>Select an Applicant to view their report.</p>
      )}
    </div>
  );
}

export default ApplicantReportPage;
