import { useState } from 'react';
import { signupHR } from '../services/api'; // Updated function to signupHR
import { Link, useNavigate } from 'react-router-dom';
import './signup.css';

function HRSignupPage() {
  const [hr_name, setHrName] = useState('');
  const [hr_email, setHrEmail] = useState('');
  const [hr_password, setHrPassword] = useState('');
  const [hr_age, setHrAge] = useState('');
  const [hr_gender, setHrGender] = useState('');
  const [quali_id, setQualiId] = useState('');
  const [hr_experience, setHrExperience] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async () => {
    const hrData = { hr_name, hr_email, hr_password, hr_age, hr_gender, quali_id, hr_experience };

    try {
      const response = await signupHR(hrData);
      console.log(response);

      if (response?.message === 'HR already exists') {
        setErrorMessage(response.message);
        setSuccessMessage('');
      } else {
        setErrorMessage('');
        setSuccessMessage('HR Signup successful! Redirecting to login...');

        // Redirect HR to login page after successful signup
        setTimeout(() => navigate('/hr-login'), 2000);
      }
    } catch (error) {
      setErrorMessage('Error signing up. Please try again.');
    }
  };

  return (
    <div className="signup-container">
      <div className="form-box">
        <h2>HR Sign Up</h2>
        <input
          type="text"
          placeholder="Full Name"
          value={hr_name}
          onChange={(e) => setHrName(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={hr_email}
          onChange={(e) => setHrEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={hr_password}
          onChange={(e) => setHrPassword(e.target.value)}
        />
        <input
          type="number"
          placeholder="Age"
          value={hr_age}
          onChange={(e) => setHrAge(e.target.value)}
        />
        <select value={hr_gender} onChange={(e) => setHrGender(e.target.value)}>
          <option value="">Select Gender</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Other">Other</option>
        </select>
        <input
          type="number"
          placeholder="Qualification ID"
          value={quali_id}
          onChange={(e) => setQualiId(e.target.value)}
        />
        <input
          type="number"
          placeholder="Experience (Years)"
          value={hr_experience}
          onChange={(e) => setHrExperience(e.target.value)}
        />
        <button onClick={handleSubmit}>Sign Up</button>

        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}

        <p>Already have an HR account? <Link to="/hr-login">Login</Link></p>
      </div>
    </div>
  );
}

export default HRSignupPage;
