import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './signup.css';

function SignupPage() {
  const [isLogin, setIsLogin] = useState(false);
  const navigate = useNavigate();

  const toggleForm = () => {
    setIsLogin(!isLogin);
  };

  const handleSignup = () => {
    console.log("Signup button clicked"); // Debugging
    navigate('/hrmenu'); // Navigate to HR Menu Page
  };

  const handleLogin = () => {
    console.log("Login button clicked"); // Debugging
    navigate('/hrmenu'); // Navigate to HR Menu Page
  };

  return (
    <div className="signup-container">
      {isLogin ? (
        <div className="form-box">
          <h2>Login</h2>
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <button type="button" onClick={handleLogin}>Login</button>
          <p onClick={toggleForm}>Don't have an account? Sign up</p>
        </div>
      ) : (
        <div className="form-box">
          <h2>Sign Up</h2>
          <input type="text" placeholder="Full Name" />
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <button type="button" onClick={handleSignup}>Sign Up</button>
          <p onClick={toggleForm}>Already have an account? Login</p>
        </div>
      )}
    </div>
  );
}

export default SignupPage;
