import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { login } from '../services/api';
import './login.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    const userData = { email, password };
    const response = await login(userData);

    // Adjust these checks to match the exact strings returned by your server
    if (response?.message === 'No account exists' || response?.message === 'Invalid password') {
      setErrorMessage(response.message);
      setSuccessMessage('');
    } else {
      setErrorMessage('');
      setSuccessMessage('Login successful');

      // If your server returns user data under "hr", store it here:
     // localStorage.setItem('user', JSON.stringify(response.hr));
      
      // If your server instead returns { user: { ... } }, use:
       localStorage.setItem('user', JSON.stringify(response.user));

      // Redirect user to the HR menu (or whichever route is correct)
      setTimeout(() => navigate('/hrmenu'), 1500);
    }
  };

  return (
    <div className="login-container">
      <div className="form-box">
        <h2>Login</h2>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>

        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}

        {successMessage && (
          <div className="success-box">
            {successMessage}
          </div>
        )}

        <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
      </div>
    </div>
  );
}

export default LoginPage;
