export const submitContract = async (contractData, token) => {
  try {
    const response = await fetch("http://localhost:5000/api/hr/contract", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`, // Send authentication token
      },
      body: JSON.stringify(contractData),
    });

    return await response.json();
  } catch (error) {
    console.error("Contract Submission Error:", error);
    return { message: "Server error" };
  }
};



const API_URL = 'http://localhost:5000/api/auth/signup';

export const signup = async (userData) => {
  const response = await fetch(API_URL, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(userData),
  });

  return response.json();
};
