import { useState, useEffect } from "react";

import axios from "axios";
import "./recommendation.css";

function RecommendationPage() {
  

  // State for dropdown options
  const [applicantIds, setApplicantIds] = useState([]);
  const [hrIds, setHrIds] = useState([]);
  const recommendations = ["Recommended", "Neutral", "Highly Recommended", "Not Recommended"];

  // State for selected values
  const [selectedApplicant, setSelectedApplicant] = useState("");
  const [selectedHR, setSelectedHR] = useState("");
  const [selectedRecommendation, setSelectedRecommendation] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch applicants
  const fetchApplicants = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/auth/applicants");
      const applicants = response.data?.data || [];
      setApplicantIds(applicants.map((applicant) => applicant?.applicant_id));
    } catch (err) {
      setError("Failed to fetch applicants: " + err.message);
      console.error("Error fetching applicants:", err);
    }
  };

  // Fetch HRs
  const fetchHRs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/auth/hrs");
      const hrs = response.data?.data || [];
      setHrIds(hrs.map((hr) => hr?.hr_id));
    } catch (err) {
      setError("Failed to fetch HR data: " + err.message);
      console.error("Error fetching HR data:", err);
    }
  };

  // Fetch data when component mounts
  useEffect(() => {
    const fetchData = async () => {
      await Promise.all([fetchApplicants(), fetchHRs()]);
      setIsLoading(false);
    };

    fetchData();
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedApplicant || !selectedHR || !selectedRecommendation) {
      alert("Please fill in all fields");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/auth/recommendations", {
        recommendationhr_id: selectedHR,
        recommendationapplicant_id: selectedApplicant,
        recommendation_description: selectedRecommendation,
      });

      if (response.data.success) {
        alert("Recommendation submitted successfully!");
        // Reset form
        setSelectedApplicant("");
        setSelectedHR("");
        setSelectedRecommendation("");
      } else {
        alert("Failed to submit recommendation: " + response.data.message);
      }
    } catch (err) {
      console.error("Error submitting recommendation:", err);
      alert("Error submitting recommendation: " + err.message);
    }
  };

  if (isLoading) return <div className="recommendation-container">Loading...</div>;
  if (error) return <div className="recommendation-container">Error: {error}</div>;

  return (
    <div className="recommendation-container">
      <h2>Provide Recommendation</h2>

      <form onSubmit={handleSubmit}>
        {/* Applicant ID Dropdown */}
        <label>👤 Select Applicant ID:</label>
        <select onChange={(e) => setSelectedApplicant(e.target.value)} value={selectedApplicant} required>
          <option value="">--Select Applicant ID--</option>
          {applicantIds.map((id) => (
            <option key={id} value={id}>
              {id}
            </option>
          ))}
        </select>

        {/* HR ID Dropdown */}
        <label>🔖 Select HR ID:</label>
        <select onChange={(e) => setSelectedHR(e.target.value)} value={selectedHR} required>
          <option value="">--Select HR ID--</option>
          {hrIds.map((id) => (
            <option key={id} value={id}>
              {id}
            </option>
          ))}
        </select>

        {/* Recommendation Type Dropdown */}
        <label>📝 Select Recommendation:</label>
        <select onChange={(e) => setSelectedRecommendation(e.target.value)} value={selectedRecommendation} required>
          <option value="">--Select Recommendation--</option>
          {recommendations.map((rec) => (
            <option key={rec} value={rec}>
              {rec}
            </option>
          ))}
        </select>

        {/* Readonly Textarea for Displaying Selection */}
        <label>📜 Recommendation Summary:</label>
        <textarea
          value={`Applicant ID: ${selectedApplicant}\nHR ID: ${selectedHR}\nRecommendation: ${selectedRecommendation}`}
          readOnly
        />

        {/* Back & Submit Buttons */}
        <div className="recommendation-buttons">
          <button type="button" className="recommendation-back" onClick={() => navigate(-1)}>
            Back
          </button>
          <button type="submit" className="recommendation-submit">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}

export default RecommendationPage;
