import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./recommendation.css";

function RecommendationPage() {
  const navigate = useNavigate();

  // State for dropdown options
  const [applicants, setApplicants] = useState([]);
  const [hrs, setHrs] = useState([]);
  const recommendations = ["Recommended", "Neutral", "Highly Recommended", "Not Recommended"];

  // State for selected values
  const [selectedApplicant, setSelectedApplicant] = useState("");
  const [selectedHR, setSelectedHR] = useState("");
  const [selectedRecommendation, setSelectedRecommendation] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch applicants
  const fetchApplicants = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/auth/applicants");
      console.log("Applicants response:", response.data); // Debug log
      if (response.data && Array.isArray(response.data)) {
        setApplicants(response.data);
      } else if (response.data && Array.isArray(response.data.data)) {
        setApplicants(response.data.data);
      } else {
        throw new Error("Invalid applicants data format");
      }
    } catch (err) {
      console.error("Error fetching applicants:", err);
      setError("Failed to fetch applicants");
    }
  };

  // Fetch HRs
  const fetchHRData = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/auth/hrs");
      console.log("HRs response:", response.data); // Debug log
      if (response.data && Array.isArray(response.data)) {
        setHrs(response.data);
      } else if (response.data && Array.isArray(response.data.data)) {
        setHrs(response.data.data);
      } else {
        throw new Error("Invalid HR data format");
      }
    } catch (err) {
      console.error("Error fetching HR data:", err);
      setError("Failed to fetch HR data");
    }
  };

  // Fetch data when component mounts
  useEffect(() => {
    const fetchData = async () => {
      try {
        await Promise.all([fetchApplicants(), fetchHRData()]);
      } catch (err) {
        console.error("Error in fetchData:", err);
        setError("Failed to load data");
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedApplicant || !selectedHR || !selectedRecommendation) {
      alert("Please fill in all fields");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/auth/recommendations", {
        recommendationhr_id: selectedHR,
        recommendationapplicant_id: selectedApplicant,
        recommendation_description: selectedRecommendation,
      });

      if (response.data?.success) {
        alert("Recommendation submitted successfully!");
        setSelectedApplicant("");
        setSelectedHR("");
        setSelectedRecommendation("");
      } else {
        alert("Failed to submit recommendation: " + (response.data?.message || "Unknown error"));
      }
    } catch (err) {
      console.error("Error submitting recommendation:", err);
      alert("Error submitting recommendation: " + (err.response?.data?.message || err.message));
    }
  };

  if (isLoading) return <div className="recommendation-container">Loading...</div>;
  if (error) return <div className="recommendation-container">Error: {error}</div>;

  return (
    <div className="recommendation-container">
      <h2>Provide Recommendation</h2>

      <form onSubmit={handleSubmit}>
        {/* Applicant Dropdown */}
        <label>ðŸ‘¤ Select Applicant:</label>
        <select 
          onChange={(e) => setSelectedApplicant(e.target.value)} 
          value={selectedApplicant} 
          required
        >
          <option value="">--Select Applicant--</option>
          {applicants.map((applicant) => (
            <option key={applicant.applicant_id} value={applicant.applicant_id}>
              {applicant.applicant_name || applicant.applicant_id}
            </option>
          ))}
        </select>

        {/* HR Dropdown */}
        <label>ðŸ”– Select HR:</label>
        <select 
          onChange={(e) => setSelectedHR(e.target.value)} 
          value={selectedHR} 
          required
        >
          <option value="">--Select HR--</option>
          {hrs.map((hr) => (
            <option key={hr.hr_id} value={hr.hr_id}>
              {hr.hr_name || hr.hr_id}
            </option>
          ))}
        </select>

        {/* Rest of your form... */}
      </form>
    </div>
  );
}

export default RecommendationPage;
