import { useState } from "react";
import "./hrreport.css"; // Import CSS

function HRReportPage() {
  const hrList = [
    { hr_id: 1, hr_name: "Alice Johnson", hr_email: "alice@example.com", hr_age: 35, hr_gender: "Female", quali_id: 101, hr_experience: 10 },
    { hr_id: 2, hr_name: "Bob Smith", hr_email: "bob@example.com", hr_age: 40, hr_gender: "Male", quali_id: 102, hr_experience: 15 },
    { hr_id: 3, hr_name: "Charlie Brown", hr_email: "charlie@example.com", hr_age: 30, hr_gender: "Male", quali_id: 103, hr_experience: 7 },
  ];

  const [selectedHrId, setSelectedHrId] = useState("");
  const [hrData, setHrData] = useState(null);

  const handleHrSelect = (e) => {
    const selectedId = parseInt(e.target.value);
    setSelectedHrId(selectedId);
    const selectedHr = hrList.find((hr) => hr.hr_id === selectedId);
    setHrData(selectedHr || null);
  };

  return (
    <div className="hr-report-container">
      <h2>HR Report</h2>

      <div className="hr-select-container">
        <label htmlFor="hr-select">Select HR ID:</label>
        <select id="hr-select" value={selectedHrId} onChange={handleHrSelect}>
          <option value="">-- Choose an HR --</option>
          {hrList.map((hr) => (
            <option key={hr.hr_id} value={hr.hr_id}>
              {hr.hr_id} - {hr.hr_name}
            </option>
          ))}
        </select>
      </div>

      {hrData ? (
        <div className="hr-details">
          <h3>HR Report - {hrData.hr_name}</h3>
          <p><strong>ID:</strong> {hrData.hr_id}</p>
          <p><strong>Email:</strong> {hrData.hr_email}</p>
          <p><strong>Age:</strong> {hrData.hr_age}</p>
          <p><strong>Gender:</strong> {hrData.hr_gender}</p>
          <p><strong>Qualification ID:</strong> {hrData.quali_id}</p>
          <p><strong>Experience:</strong> {hrData.hr_experience} years</p>
        </div>
      ) : (
        <p>Select an HR to view their report.</p>
      )}
    </div>
  );
}

export default HRReportPage;
