import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getApplicants, getHRs, createRecommendation } from '../services/api';
import './recommendation.css';

function RecommendationPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    recommendationhr_id: "",
    recommendationapplicant_id: "",
    recommendation_description: ""
  });
  const [applicants, setApplicants] = useState([]);
  const [hrs, setHrs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  // Fetch applicants and HRs from database
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [applicantsData, hrsData] = await Promise.all([
          getApplicants(),
          getHRs()
        ]);
        setApplicants(applicantsData.data || applicantsData);
        setHrs(hrsData.data || hrsData);
        setLoading(false);
      } catch (err) {
        setError("Failed to load data");
        setLoading(false);
        console.error("Error fetching data:", err);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async () => {
    if (!formData.recommendationhr_id || !formData.recommendationapplicant_id || !formData.recommendation_description) {
      setError("Please fill all fields");
      return;
    }

    try {
      await createRecommendation(formData);
      setSuccess(true);
      setError(null);
      // Reset form after successful submission
      setFormData({
        recommendationhr_id: "",
        recommendationapplicant_id: "",
        recommendation_description: ""
      });
      // Hide success message after 3 seconds
      setTimeout(() => setSuccess(false), 3000);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to submit recommendation");
      console.error("Submission error:", err);
    }
  };

  if (loading) return <div className="loading">Loading data...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="recommendation-container">
      <h2>Provide Recommendation</h2>

      {/* Applicant Dropdown */}
      <div className="form-group">
        <label>üë§ Select Applicant:</label>
        <select 
          name="recommendationapplicant_id" 
          onChange={handleChange} 
          value={formData.recommendationapplicant_id}
          required
        >
          <option value="">-- Select Applicant --</option>
          {applicants.map(applicant => (
            <option 
              key={applicant.applicant_id} 
              value={applicant.applicant_id}
            >
              {applicant.applicant_name} (ID: {applicant.applicant_id})
            </option>
          ))}
        </select>
      </div>

      {/* HR Dropdown */}
      <div className="form-group">
        <label>üîñ Select HR Manager:</label>
        <select 
          name="recommendationhr_id" 
          onChange={handleChange} 
          value={formData.recommendationhr_id}
          required
        >
          <option value="">-- Select HR Manager --</option>
          {hrs.map(hr => (
            <option 
              key={hr.hr_id} 
              value={hr.hr_id}
            >
              {hr.hr_name} (ID: {hr.hr_id})
            </option>
          ))}
        </select>
      </div>

      {/* Recommendation Description */}
      <div className="form-group">
        <label>üìù Recommendation Description:</label>
        <textarea
          name="recommendation_description"
          value={formData.recommendation_description}
          onChange={handleChange}
          placeholder="Enter detailed recommendation..."
          rows="5"
          required
        />
      </div>

      {/* Status Messages */}
      {success && (
        <div className="success-message">
          Recommendation submitted successfully!
        </div>
      )}

      {/* Action Buttons */}
      <div className="recommendation-buttons">
        <button 
          className="recommendation-back" 
          onClick={() => navigate(-1)}
        >
          Back
        </button>
        <button 
          className="recommendation-submit" 
          onClick={handleSubmit}
          disabled={!formData.recommendationhr_id || !formData.recommendationapplicant_id || !formData.recommendation_description}
        >
          Submit Recommendation
        </button>
      </div>
    </div>
  );
}

export default RecommendationPage;
