import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './contract.css';

function ContractPage() {
  const navigate = useNavigate();
  const [jobs, setJobs] = useState([]);
  const [jobDetails, setJobDetails] = useState("");
  const [contractData, setContractData] = useState({
    job_id: "",
    salary: "",
    probation_period: "",
    contract_start_date: "",
    contract_end_date: "",
    benefits: ""
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  // Get the HR ID from localStorage or sessionStorage
  const hr_id = localStorage.getItem('hr_id') || sessionStorage.getItem('hr_id');

  // Fetch jobs from the database
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/auth/jobs");
        setJobs(response.data.data || []);
      } catch (err) {
        console.error("Error fetching jobs:", err);
        setError("Failed to fetch jobs. Please try again later.");
      }
    };
    fetchJobs();
  }, []);

  // Handle job selection and fetch job details
  const handleJobChange = async (e) => {
    const selectedJobId = e.target.value;
    setContractData({ ...contractData, job_id: selectedJobId });
    
    if (selectedJobId) {
      try {
        const response = await axios.get(`http://localhost:5000/api/auth/jobs/${selectedJobId}`);
        const job = response.data.data;
        
        // Format job details for display
        const details = `Title: ${job.job_title}\nCompany: ${job.company_name}\nType: ${job.job_type}\nDescription: ${job.job_description}\nQualifications: ${job.qualification_skills_required}\nExperience: ${job.job_experience} years\nCGPA Required: ${job.cgpa_required}`;
        
        setJobDetails(details);
      } catch (err) {
        console.error("Error fetching job details:", err);
        setJobDetails("Failed to load job details.");
      }
    } else {
      setJobDetails("");
    }
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setContractData({ ...contractData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate form data
    if (!contractData.job_id || !contractData.salary || !contractData.probation_period || 
        !contractData.contract_start_date || !contractData.contract_end_date || !contractData.benefits) {
      setError("Please fill in all fields");
      return;
    }
    
    // Reset states
    setError(null);
    setSuccess(null);
    setLoading(true);
    
    try {
      const response = await axios.post(
        "http://localhost:5000/api/auth/create",
        {
          ...contractData,
          hr_id: parseInt(hr_id)
        },
        {
          headers: { "Content-Type": "application/json" }
        }
      );
      
      console.log("Contract creation response:", response);
      
      if (response.data && response.data.message) {
        setSuccess(response.data.message);
        // Reset form after successful submission
        setContractData({
          job_id: "",
          salary: "",
          probation_period: "",
          contract_start_date: "",
          contract_end_date: "",
          benefits: ""
        });
        setJobDetails("");
      }
    } catch (err) {
      console.error("Error creating contract:", err);
      setError(err.response?.data?.message || "Failed to create contract. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="contract-page-container">
      <button className="contract-back-button" onClick={() => navigate(-1)}>Back</button>
      <h2>Create New Contract</h2>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <form onSubmit={handleSubmit}>
        <div className="contract-content">
          {/* Left Column - Form */}
          <div className="contract-form">
            <label>Job ID</label>
            <select 
              name="job_id"
              value={contractData.job_id}
              onChange={handleJobChange}
              required
            >
              <option value="" disabled>Select Job ID</option>
              {jobs.map(job => (
                <option key={job.jobid} value={job.jobid}>
                  {job.jobid} - {job.job_title}
                </option>
              ))}
            </select>

            <label>Salary</label>
            <input
              type="text"
              name="salary"
              placeholder="Enter salary amount"
              value={contractData.salary}
              onChange={handleInputChange}
              required
            />

            <label>Probation Period (months)</label>
            <input
              type="number"
              name="probation_period"
              min="0"
              placeholder="Enter probation period"
              value={contractData.probation_period}
              onChange={handleInputChange}
              required
            />

            <label>Contract Start Date</label>
            <input
              type="date"
              name="contract_start_date"
              value={contractData.contract_start_date}
              onChange={handleInputChange}
              required
            />

            <label>Contract End Date</label>
            <input
              type="date"
              name="contract_end_date"
              value={contractData.contract_end_date}
              onChange={handleInputChange}
              required
            />

            <label>Benefits</label>
            <textarea
              name="benefits"
              placeholder="Enter contract benefits"
              value={contractData.benefits}
              onChange={handleInputChange}
              rows="3"
              required
            ></textarea>

            <button 
              type="submit" 
              className="contract-submit-button"
              disabled={loading}
            >
              {loading ? "Creating..." : "Create Contract"}
            </button>
          </div>

          {/* Right Column - Readonly Details */}
          <div className="contract-details">
            <h3>Job Details</h3>
            <textarea 
              readOnly 
              value={jobDetails}
              rows="12"
            ></textarea>
          </div>
        </div>
      </form>
    </div>
  );
}

export default ContractPage;
