import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { createContract } from '../services/api';
import "./contract.css";

function ContractPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    job_id: "",
    hr_id: "",
    salary: "",
    probation_period: "",
    start_date: "",
    end_date: "",
    benefits: "",
  });

  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [createdContract, setCreatedContract] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isMounted, setIsMounted] = useState(true);

  // Initialize form and check authentication
  useEffect(() => {
    setIsMounted(true);
    const hr_id = localStorage.getItem("hr_id") || sessionStorage.getItem("hr_id");
    
    if (!hr_id) {
      setErrorMessage("HR authentication failed. Please login again.");
      navigate("/login"); // Redirect if not authenticated
      return;
    }

    setFormData(prev => ({ ...prev, hr_id }));

    return () => setIsMounted(false);
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const validateForm = () => {
    // Clear previous messages
    setErrorMessage("");
    setSuccessMessage("");

    // Required fields check
    const requiredFields = ['job_id', 'salary', 'probation_period', 'start_date', 'end_date'];
    const emptyFields = requiredFields.filter(field => !formData[field].toString().trim());

    if (emptyFields.length > 0) {
      setErrorMessage(`Missing required fields: ${emptyFields.join(', ')}`);
      return false;
    }

    // Numeric validation
    if (isNaN(formData.salary) || Number(formData.salary) <= 0) {
      setErrorMessage("Salary must be a positive number");
      return false;
    }

    if (isNaN(formData.probation_period) || Number(formData.probation_period) < 0) {
      setErrorMessage("Probation period must be 0 or more days");
      return false;
    }

    // Date validation
    const startDate = new Date(formData.start_date);
    const endDate = new Date(formData.end_date);

    if (startDate >= endDate) {
      setErrorMessage("End date must be after start date");
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isSubmitting || !isMounted) return;

    if (!validateForm()) return;

    try {
      setIsSubmitting(true);

      const payload = {
        ...formData,
        salary: Number(formData.salary),
        probation_period: Number(formData.probation_period),
        start_date: new Date(formData.start_date).toISOString(),
        end_date: new Date(formData.end_date).toISOString()
      };

      const response = await createContract(payload);
      
      if (!isMounted) return;

      if (response.error) {
        throw new Error(response.error);
      }

      setCreatedContract(response);
      setSuccessMessage("Contract created successfully!");
      
      // Reset form (keep hr_id)
      setFormData(prev => ({
        ...prev,
        job_id: "",
        salary: "",
        probation_period: "",
        start_date: "",
        end_date: "",
        benefits: ""
      }));

    } catch (error) {
      console.error("Contract creation failed:", error);
      setErrorMessage(error.message || "Failed to create contract. Please try again.");
    } finally {
      if (isMounted) setIsSubmitting(false);
    }
  };

  const handleBack = () => navigate(-1);

  return (
    <div className="contract-page-container">
      <div className="contract-header">
        <button className="contract-back-button" onClick={handleBack}>Back</button>
        <h2>Create New Contract</h2>
      </div>

      {/* Status Messages */}
      {errorMessage && (
        <div className="error-message">
          {errorMessage}
        </div>
      )}

      {successMessage && (
        <div className="success-message">
          {successMessage}
        </div>
      )}

      <div className="contract-content-wrapper">
        {/* Form Section */}
        <div className="contract-form-section">
          <form className="contract-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Job ID *</label>
              <input 
                type="text" 
                name="job_id" 
                value={formData.job_id} 
                onChange={handleChange} 
                required
              />
            </div>

            <div className="form-group">
              <label>Salary *</label>
              <input 
                type="number" 
                name="salary" 
                value={formData.salary} 
                onChange={handleChange} 
                min="0"
                step="0.01"
                required
              />
            </div>

            <div className="form-group">
              <label>Probation Period (days) *</label>
              <input 
                type="number" 
                name="probation_period" 
                value={formData.probation_period} 
                onChange={handleChange} 
                min="0"
                required
              />
            </div>

            <div className="form-group">
              <label>Contract Start Date *</label>
              <input 
                type="date" 
                name="start_date" 
                value={formData.start_date} 
                onChange={handleChange} 
                required
                min={new Date().toISOString().split('T')[0]}
              />
            </div>

            <div className="form-group">
              <label>Contract End Date *</label>
              <input 
                type="date" 
                name="end_date" 
                value={formData.end_date} 
                onChange={handleChange} 
                required
                min={formData.start_date || new Date().toISOString().split('T')[0]}
              />
            </div>

            <div className="form-group">
              <label>Benefits</label>
              <textarea 
                name="benefits" 
                value={formData.benefits} 
                onChange={handleChange} 
                rows="3"
              />
            </div>

            <button 
              type="submit" 
              className="contract-submit-button" 
              disabled={isSubmitting}
            >
              {isSubmitting ? "Creating..." : "Create Contract"}
            </button>
          </form>
        </div>

        {/* Contract Display Section */}
        <div className="contract-display-section">
          <div className="contract-display-area">
            <h3>Created Contract Details</h3>
            
            {createdContract ? (
              <div className="contract-details">
                <div className="detail-item">
                  <span className="detail-label">Job ID:</span>
                  <span className="detail-value">{createdContract.job_id}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">HR ID:</span>
                  <span className="detail-value">{createdContract.hr_id}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">Salary:</span>
                  <span className="detail-value">${createdContract.salary.toLocaleString()}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">Probation Period:</span>
                  <span className="detail-value">{createdContract.probation_period} days</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">Start Date:</span>
                  <span className="detail-value">
                    {new Date(createdContract.start_date).toLocaleDateString()}
                  </span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">End Date:</span>
                  <span className="detail-value">
                    {new Date(createdContract.end_date).toLocaleDateString()}
                  </span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">Benefits:</span>
                  <span className="detail-value">{createdContract.benefits || "None specified"}</span>
                </div>
              </div>
            ) : (
              <div className="no-contract">
                <p>No contract created yet.</p>
                <p>Fill the form and submit to create a contract.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ContractPage;
