import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./recommendation.css";

function RecommendationPage() {
  const navigate = useNavigate();

  // State for dropdown options
  const [applicants, setApplicants] = useState([]);
  const [hrs, setHrs] = useState([]);
  const recommendations = ["Recommended", "Neutral", "Highly Recommended", "Not Recommended"];

  // State for selected values
  const [selectedApplicant, setSelectedApplicant] = useState("");
  const [selectedHR, setSelectedHR] = useState("");
  const [selectedRecommendation, setSelectedRecommendation] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitStatus, setSubmitStatus] = useState({ type: null, message: "" });

  // Fetch applicants
  const fetchApplicants = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/auth/applicants");
      console.log("Applicants response:", response.data);
      
      // Handle different response structures
      const applicantsData = Array.isArray(response.data) 
        ? response.data 
        : response.data?.data || [];
      
      if (!applicantsData.length) {
        console.warn("No applicants data received");
      }
      setApplicants(applicantsData);
    } catch (err) {
      console.error("Error fetching applicants:", err);
      setError("Failed to fetch applicants. Please try again later.");
    }
  };

  // Fetch HRs
  const fetchHRData = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/auth/hrs");
      console.log("HRs response:", response.data);
      
      // Handle different response structures
      const hrsData = Array.isArray(response.data) 
        ? response.data 
        : response.data?.data || [];
      
      if (!hrsData.length) {
        console.warn("No HRs data received");
      }
      setHrs(hrsData);
    } catch (err) {
      console.error("Error fetching HR data:", err);
      setError("Failed to fetch HR data. Please try again later.");
    }
  };

  // Fetch data when component mounts
  useEffect(() => {
    const fetchData = async () => {
      try {
        await Promise.all([fetchApplicants(), fetchHRData()]);
      } catch (err) {
        console.error("Error in fetchData:", err);
        setError("Failed to load initial data. Please refresh the page.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitStatus({ type: null, message: "" });
  
    // Validation
    if (!selectedApplicant || !selectedHR || !selectedRecommendation) {
      setSubmitStatus({ type: "error", message: "Please fill in all fields" });
      return;
    }
  
    try {
      const response = await axios.post("http://localhost:5000/api/auth/recommendations", {
        recommendationhr_id: selectedHR,
        recommendationapplicant_id: selectedApplicant,
        recommendation_description: selectedRecommendation,
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
  
      console.log("Response:", response.data);
  
      if (response.data?.success) {
        setSubmitStatus({ 
          type: "success", 
          message: "Recommendation submitted successfully!" 
        });
        // Reset form
        setSelectedApplicant("");
        setSelectedHR("");
        setSelectedRecommendation("");
      } else {
        setSubmitStatus({
          type: "error",
          message: response.data?.message || "Submission failed with no error message"
        });
      }
    } catch (err) {
      console.error("Full error:", err);
      console.error("Error response:", err.response?.data);
      
      setSubmitStatus({
        type: "error",
        message: err.response?.data?.error || 
               err.response?.data?.message || 
               err.message || 
               "Failed to submit recommendation"
      });
    }
  };

  if (isLoading) {
    return (
      <div className="recommendation-container">
        <div className="loading-spinner"></div>
        <p>Loading data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="recommendation-container error">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => window.location.reload()}>Refresh Page</button>
      </div>
    );
  }

  return (
    <div className="recommendation-container">
      <h2>Provide Recommendation</h2>

      {submitStatus.type && (
        <div className={`status-message ${submitStatus.type}`}>
          {submitStatus.message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* Applicant Dropdown */}
        <div className="form-group">
          <label>👤 Select Applicant:</label>
          <select
            onChange={(e) => setSelectedApplicant(e.target.value)}
            value={selectedApplicant}
            required
            disabled={!applicants.length}
          >
            <option value="">--Select Applicant--</option>
            {applicants.map((applicant) => (
              <option key={applicant.applicant_id} value={applicant.applicant_id}>
                {applicant.applicant_name || `Applicant ${applicant.applicant_id}`}
              </option>
            ))}
          </select>
          {!applicants.length && (
            <p className="form-hint">No applicants available</p>
          )}
        </div>

        {/* HR Dropdown */}
        <div className="form-group">
          <label>🔖 Select HR:</label>
          <select
            onChange={(e) => setSelectedHR(e.target.value)}
            value={selectedHR}
            required
            disabled={!hrs.length}
          >
            <option value="">--Select HR--</option>
            {hrs.map((hr) => (
              <option key={hr.hr_id} value={hr.hr_id}>
                {hr.hr_name || `HR ${hr.hr_id}`}
              </option>
            ))}
          </select>
          {!hrs.length && (
            <p className="form-hint">No HRs available</p>
          )}
        </div>

        {/* Recommendation Dropdown */}
        <div className="form-group">
          <label>📝 Select Recommendation:</label>
          <select
            onChange={(e) => setSelectedRecommendation(e.target.value)}
            value={selectedRecommendation}
            required
          >
            <option value="">--Select Recommendation--</option>
            {recommendations.map((rec) => (
              <option key={rec} value={rec}>
                {rec}
              </option>
            ))}
          </select>
        </div>

        {/* Summary */}
        <div className="form-group">
          <label>📜 Recommendation Summary:</label>
          <textarea
            value={
              `Applicant: ${applicants.find(a => a.applicant_id === selectedApplicant)?.applicant_name || selectedApplicant || "Not selected"}\n` +
              `HR: ${hrs.find(h => h.hr_id === selectedHR)?.hr_name || selectedHR || "Not selected"}\n` +
              `Recommendation: ${selectedRecommendation || "Not selected"}`
            }
            readOnly
            rows="4"
          />
        </div>

        {/* Form Actions */}
        <div className="form-actions">
          <button
            type="button"
            className="secondary-button"
            onClick={() => navigate(-1)}
          >
            Back
          </button>
          <button
            type="submit"
            className="primary-button"
            disabled={isLoading || !applicants.length || !hrs.length}
          >
            {isLoading ? "Submitting..." : "Submit Recommendation"}
          </button>
        </div>
      </form>
    </div>
  );
}

export default RecommendationPage;
