// Updated ContractPage component with better error handling
import React, { useState, useEffect } from "react"; 
import { useNavigate } from "react-router-dom"; 
import { createContract } from '../services/api'; 
import "./contract.css";  
 
function ContractPage() { 
  const navigate = useNavigate(); 
  const [formData, setFormData] = useState({ 
    job_id: "", 
    hr_id: "", 
    salary: "", 
    probation_period: "", 
    start_date: "", 
    end_date: "", 
    benefits: "", 
  }); 
  
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const hr_id = localStorage.getItem("hr_id") || sessionStorage.getItem("hr_id") || 1;
    setFormData(prevData => ({
      ...prevData,
      hr_id: hr_id
    }));
  }, []);
 
  const handleChange = (e) => { 
    setFormData((prevData) => ({ 
      ...prevData, 
      [e.target.name]: e.target.value, 
    })); 
  }; 
 
  const handleSubmit = async () => {
    // Reset messages
    setSuccessMessage("");
    setErrorMessage("");
    
    // Check visible fields
    const visibleFields = ['job_id', 'salary', 'probation_period', 'start_date', 'end_date', 'benefits'];
    const emptyFields = visibleFields.filter(field => !formData[field]);
    
    if (emptyFields.length > 0) { 
      setErrorMessage("Please fill in all fields before submitting.");
      return; 
    }
    
    setIsSubmitting(true);
 
    try {
      // Debug: Log the URL you're targeting
      console.log("API URL Base:", window.location.origin);
      
      // Format the data
      const contractData = {
        job_id: parseInt(formData.job_id, 10),
        hr_id: parseInt(formData.hr_id, 10),
        salary: formData.salary,
        probation_period: parseInt(formData.probation_period, 10),
        start_date: formData.start_date,
        end_date: formData.end_date,
        benefits: formData.benefits
      };
      
      console.log("Submitting contract data:", contractData);
      
      // Direct API call rather than relying on service function
      const response = await fetch('/api/contract/create', { // Check if the path should be /contract/create or /contracts/create
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(contractData),
      });
      
      // Process response
      if (!response.ok) {
        const contentType = response.headers.get("content-type");
        if (contentType && contentType.includes("application/json")) {
          const errorData = await response.json();
          throw new Error(errorData.message || 'Failed to create contract');
        } else {
          const textError = await response.text();
          console.error("Non-JSON response:", textError.substring(0, 150));
          throw new Error("Server returned an error. Status: " + response.status);
        }
      }
      
      const result = await response.json();
      setSuccessMessage("Contract Created Successfully!");
      console.log("Contract creation response:", result);
      
      // Clear form fields after successful submission
      setFormData({
        job_id: "", 
        hr_id: formData.hr_id, // Keep the HR ID
        salary: "", 
        probation_period: "", 
        start_date: "", 
        end_date: "", 
        benefits: "", 
      });
    } catch (error) { 
      console.error("Error creating contract:", error);
      setErrorMessage("Failed to create contract: " + error.message); 
    } finally {
      setIsSubmitting(false);
    }
  }; 
  
  const handleBack = () => {
    navigate(-1);
  };
 
  return ( 
    <div className="contract-page-container"> 
      <button className="contract-back-button" onClick={handleBack}>Back</button> 
      <h2>Contract</h2>
      
      {successMessage && (
        <div className="success-message" style={{ color: 'green', padding: '10px', marginBottom: '10px', backgroundColor: '#e6f7e6', border: '1px solid green', borderRadius: '4px' }}>
          {successMessage}
        </div>
      )}
      
      {errorMessage && (
        <div className="error-message" style={{ color: 'red', padding: '10px', marginBottom: '10px', backgroundColor: '#ffeeee', border: '1px solid red', borderRadius: '4px' }}>
          {errorMessage}
        </div>
      )}
      
      <div className="contract-content"> 
        <div className="contract-form"> 
          <label>Job ID</label> 
          <input type="number" name="job_id" value={formData.job_id} onChange={handleChange} /> 
 
          <label>Salary</label> 
          <input type="text" name="salary" value={formData.salary} onChange={handleChange} /> 
 
          <label>Probation Period (days)</label> 
          <input type="number" name="probation_period" value={formData.probation_period} onChange={handleChange} /> 
 
          <label>Contract Start Date</label> 
          <input type="date" name="start_date" value={formData.start_date} onChange={handleChange} /> 
 
          <label>Contract End Date</label> 
          <input type="date" name="end_date" value={formData.end_date} onChange={handleChange} /> 
 
          <label>Benefits</label> 
          <input type="text" name="benefits" value={formData.benefits} onChange={handleChange} /> 
        </div>
      </div> 
 
      <button 
        className="contract-submit-button" 
        onClick={handleSubmit}
        disabled={isSubmitting}
      >
        {isSubmitting ? 'Submitting...' : 'Submit'}
      </button> 
    </div> 
  ); 
} 
 
export default ContractPage;
