import { useState } from "react";
import "./recruiterreport.css"; // Import CSS

function RecruiterReportPage() {
  const recruiterList = [
    { recruiter_id: 1, recruiter_name: "Sophia Wilson", recruiter_email: "sophia@example.com", recruiter_age: 35, recruiter_gender: "Female", quali_id: 301, recruiter_experience: 10 },
    { recruiter_id: 2, recruiter_name: "Michael Johnson", recruiter_email: "michael@example.com", recruiter_age: 40, recruiter_gender: "Male", quali_id: 302, recruiter_experience: 12 },
    { recruiter_id: 3, recruiter_name: "Linda Roberts", recruiter_email: "linda@example.com", recruiter_age: 38, recruiter_gender: "Female", quali_id: 303, recruiter_experience: 9 },
  ];

  const [selectedRecruiterId, setSelectedRecruiterId] = useState("");
  const [recruiterData, setRecruiterData] = useState(null);

  const handleRecruiterSelect = (e) => {
    const selectedId = parseInt(e.target.value);
    setSelectedRecruiterId(selectedId);
    const selectedRecruiter = recruiterList.find((recruiter) => recruiter.recruiter_id === selectedId);
    setRecruiterData(selectedRecruiter || null);
  };

  return (
    <div className="recruiter-report-container">
      <h2>Recruiter Report</h2>

      <div className="recruiter-select-container">
        <label htmlFor="recruiter-select">Select Recruiter ID:</label>
        <select id="recruiter-select" value={selectedRecruiterId} onChange={handleRecruiterSelect}>
          <option value="">-- Choose a Recruiter --</option>
          {recruiterList.map((recruiter) => (
            <option key={recruiter.recruiter_id} value={recruiter.recruiter_id}>
              {recruiter.recruiter_id} - {recruiter.recruiter_name}
            </option>
          ))}
        </select>
      </div>

      {recruiterData ? (
        <div className="recruiter-details">
          <h3>Recruiter Report - {recruiterData.recruiter_name}</h3>
          <p><strong>ID:</strong> {recruiterData.recruiter_id}</p>
          <p><strong>Email:</strong> {recruiterData.recruiter_email}</p>
          <p><strong>Age:</strong> {recruiterData.recruiter_age}</p>
          <p><strong>Gender:</strong> {recruiterData.recruiter_gender}</p>
          <p><strong>Qualification ID:</strong> {recruiterData.quali_id}</p>
          <p><strong>Experience:</strong> {recruiterData.recruiter_experience} years</p>
        </div>
      ) : (
        <p>Select a Recruiter to view their report.</p>
      )}
    </div>
  );
}

export default RecruiterReportPage;
