import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginHR } from '../services/api'; // Updated function to loginHR
import { Link } from 'react-router-dom';
import './login.css';

function HRLoginPage() {
  const [hr_email, setHrEmail] = useState('');
  const [hr_password, setHrPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    const hrData = { hr_email, hr_password };
    const response = await loginHR(hrData); // Use loginHR for authentication

    if (response?.message === 'No HR account found' || response?.message === 'Invalid password') {
      setErrorMessage(response.message);
      setSuccessMessage('');
    } else {
      setErrorMessage('');
      setSuccessMessage('HR Login successful');

      // Store HR data in local storage (for session persistence)
      localStorage.setItem('hr', JSON.stringify(response.hr));

      // Redirect HR to Dashboard
      setTimeout(() => navigate('/hr-dashboard'), 1500);
    }
  };

  return (
    <div className="login-container">
      <div className="form-box">
        <h2>HR Login</h2>
        <input
          type="email"
          placeholder="HR Email"
          value={hr_email}
          onChange={(e) => setHrEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="HR Password"
          value={hr_password}
          onChange={(e) => setHrPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>

        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}

        {successMessage && (
          <div className="success-box">
            {successMessage}
          </div>
        )}

        <p>Don't have an HR account? <Link to="/hr-signup">Sign Up</Link></p>
      </div>
    </div>
  );
}

export default HRLoginPage;
