import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './recommendation.css';

function RecommendationPage() {
  const navigate = useNavigate();

  // State for dropdown options
  const [applicants, setApplicants] = useState([]);
  const [hrs, setHrs] = useState([]);
  const recommendations = ["Recommended", "Neutral", "Highly Recommended", "Not Recommended"];

  // State for selected values
  const [selectedApplicant, setSelectedApplicant] = useState("");
  const [selectedHR, setSelectedHR] = useState("");
  const [selectedRecommendation, setSelectedRecommendation] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitStatus, setSubmitStatus] = useState(null);

  // Fetch HR and Applicant data on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch applicants
        const applicantsResponse = await axios.get("http://localhost:5000/api/auth/applicants");
        if (!applicantsResponse.data?.success) {
          throw new Error(applicantsResponse.data?.message || "Failed to fetch applicants");
        }
        setApplicants(applicantsResponse.data.data || []);

        // Fetch HRs
        const hrsResponse = await axios.get("http://localhost:5000/api/auth/hrs");
        if (!hrsResponse.data?.success) {
          throw new Error(hrsResponse.data?.message || "Failed to fetch HRs");
        }
        setHrs(hrsResponse.data.data || []);

        setIsLoading(false);
      } catch (err) {
        setError(err.message || "Failed to load data");
        setIsLoading(false);
        console.error('Error fetching data:', err);
      }
    };

    fetchData();
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitStatus(null);
    
    if (!selectedApplicant || !selectedHR || !selectedRecommendation) {
      setSubmitStatus({ type: 'error', message: 'Please fill in all fields' });
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/auth/recommendations", {
        recommendationhr_id: selectedHR,
        recommendationapplicant_id: selectedApplicant,
        recommendation_description: selectedRecommendation
      });

      if (response.data?.success) {
        setSubmitStatus({ type: 'success', message: 'Recommendation submitted successfully!' });
        // Reset form
        setSelectedApplicant("");
        setSelectedHR("");
        setSelectedRecommendation("");
      } else {
        setSubmitStatus({ 
          type: 'error', 
          message: response.data?.message || 'Submission failed without error message' 
        });
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || 
                         err.message || 
                         'Failed to submit recommendation';
      setSubmitStatus({ type: 'error', message: errorMessage });
      console.error('Error submitting recommendation:', err);
    }
  };

  if (isLoading) return <div className="recommendation-container">Loading...</div>;
  if (error) return <div className="recommendation-container error">Error: {error}</div>;

  return (
    <div className="recommendation-container">
      <h2>Provide Recommendation</h2>

      {submitStatus && (
        <div className={`alert ${submitStatus.type}`}>
          {submitStatus.message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* Applicant Dropdown */}
        <label>👤 Select Applicant:</label>
        <select 
          onChange={(e) => setSelectedApplicant(e.target.value)} 
          value={selectedApplicant}
          required
        >
          <option value="">--Select Applicant--</option>
          {applicants.map((applicant) => (
            <option key={applicant.applicant_id} value={applicant.applicant_id}>
              {applicant.applicant_name} ({applicant.applicant_id})
            </option>
          ))}
        </select>

        {/* HR Dropdown */}
        <label>🔖 Select HR:</label>
        <select 
          onChange={(e) => setSelectedHR(e.target.value)} 
          value={selectedHR}
          required
        >
          <option value="">--Select HR--</option>
          {hrs.map((hr) => (
            <option key={hr.hr_id} value={hr.hr_id}>
              {hr.hr_name} ({hr.hr_id})
            </option>
          ))}
        </select>

        {/* Recommendation Type Dropdown */}
        <label>📝 Select Recommendation:</label>
        <select 
          onChange={(e) => setSelectedRecommendation(e.target.value)} 
          value={selectedRecommendation}
          required
        >
          <option value="">--Select Recommendation--</option>
          {recommendations.map((rec) => (
            <option key={rec} value={rec}>{rec}</option>
          ))}
        </select>

        {/* Readonly Textarea for Displaying Selection */}
        <label>📜 Recommendation Summary:</label>
        <textarea 
          value={
            `Applicant: ${applicants.find(a => a.applicant_id === selectedApplicant)?.applicant_name || ''} (${selectedApplicant})\n` +
            `HR: ${hrs.find(h => h.hr_id === selectedHR)?.hr_name || ''} (${selectedHR})\n` +
            `Recommendation: ${selectedRecommendation}`
          } 
          readOnly
        />

        {/* Back & Submit Buttons */}
        <div className="recommendation-buttons">
          <button 
            type="button" 
            className="recommendation-back" 
            onClick={() => navigate(-1)}
          >
            Back
          </button>
          <button 
            type="submit" 
            className="recommendation-submit"
            disabled={isLoading}
          >
            {isLoading ? 'Submitting...' : 'Submit'}
          </button>
        </div>
      </form>
    </div>
  );
}

export default RecommendationPage;
